document.addEventListener("DOMContentLoaded",function(){const e=document.getElementById("back-to-top"),t=document.querySelector(".links");window.addEventListener("scroll",function(){if(t){t.getBoundingClientRect().bottom<0?e.classList.add("visible"):e.classList.remove("visible")}}),e.addEventListener("click",function(){window.scrollTo({top:0,behavior:"smooth"})})});
